import React from 'react'
import { useState, useEffect } from 'react'
import { Box } from '@mui/system'
import { Drawer, IconButton, List, ListItemButton, Typography } from '@mui/material'
import LogoutlinedIcon from '@mui/icons-material/LogoutOutlined';
import AddBoxOutlinedIcon from '@mui/icons-material/AddBoxOutlined';
import assets from '../../assets/index';
import { Link, useNavigate, useParams } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import memoApi from '../../api/memo';
import { setMemo } from '../../redux/features/memoSlice';

const Sidebar = () => {
    const [activeIndex, setActiveIndex] = useState(0);
    const dispatch = useDispatch();
    const navigate = useNavigate();
    const { memoId } = useParams();
    const user = useSelector(state => state.user.value);
    const memos = useSelector(state => state.memo.value);

    const logout = () => {
        localStorage.removeItem('token');
        navigate('/login');
    };

    useEffect(() => {
        const getMemos = async () => {
            try {
                const res = await memoApi.getAll();
                dispatch(setMemo(res));
            } catch (error) {
                alert(error.message);
            }
        }
        getMemos();
    }, [dispatch])

    useEffect(() => {
        const activeIndex = memos.findIndex((e) => e._id === memoId);
        setActiveIndex(activeIndex);
    }, [memos, memoId, navigate])

    const addMemo = async () => {
        try {
            const res = await memoApi.create();
            const newMemos = [res, ...memos];
            dispatch(setMemo(newMemos));
            navigate(`/memo/${res._id}`);
        } catch (error) {
            alert(error);
        }
    }

    return (
        <Drawer
            container={window.document.body}
            variant="permanent"
            open={true}
            sx={{ width: 350, height: "100vh" }}
        >
            <List sx={{ width: 350, height: "100vh", backgroundColor: assets.colors.secondary }}>
                <ListItemButton>
                    <Box sx={{ width: "100%", display: "flex", alignItems: "center", justifyContent: "space-between" }}>
                        <Typography variant='body2' fontWeight="700">
                            {user.username}
                        </Typography>
                        <IconButton onClick={logout}>
                            <LogoutlinedIcon />
                        </IconButton>
                    </Box>
                </ListItemButton>
                <Box sx={{ paddingTop: "10px" }}></Box>
                <ListItemButton>
                    <Box sx={{ width: "100%", display: "flex", alignItems: "center", justifyContent: "space-between" }}>
                        <Typography variant='body2' fontWeight="700">
                            お気に入り
                        </Typography>
                    </Box>
                </ListItemButton>
                <Box sx={{ paddingTop: "10px" }}></Box>
                <ListItemButton>
                    <Box sx={{ width: "100%", display: "flex", alignItems: "center", justifyContent: "space-between" }}>
                        <Typography variant='body2' fontWeight="700">
                            プライベート
                        </Typography>
                        <IconButton onClick={addMemo}>
                            <AddBoxOutlinedIcon fontSize="small" />
                        </IconButton>
                    </Box>
                </ListItemButton>
                {memos.map((memo, index) => (
                    <ListItemButton
                        sx={{ pl: "20px" }}
                        component={Link}
                        to={`/memo/${memo._id}`}
                        key={memo._id}
                        selected={index === activeIndex}
                    >
                        <Typography>{memo.icon} {memo.title}</Typography>
                    </ListItemButton>
                ))}
            </List>
        </Drawer>
    )
}

export default Sidebar
